getgenv().InfinityMoney = true task.spawn(function() while getgenv().InfinityMoney do local r for _,s in ipairs({game:GetService("ReplicatedStorage"),game:GetService("Workspace"),game:GetService("Players")}) do r=s:FindFirstChild("RequestPlayWithDeveloperAward",true) if r then break end end if r then pcall(function() if r:IsA("RemoteEvent") then r:FireServer() else r:InvokeServer() end end) end task.wait() end end)
